<?php require_once __DIR__.'/../core/helper.php'; require_once __DIR__.'/../core/db.php'; nxi_require_login(); if($_SESSION['user']['role']!=='advertiser') die('forbidden'); $db=nxi_db(); if($_SERVER['REQUEST_METHOD']==='POST'){ $title=$db->real_escape_string($_POST['title']??''); $type=$db->real_escape_string($_POST['type']??'html'); $html=$db->real_escape_string($_POST['html']??''); $image=$db->real_escape_string($_POST['image']??''); $url=$db->real_escape_string($_POST['click_url']??''); $db->query("INSERT INTO ads(advertiser_id,type,title,html,image,click_url,status) VALUES(".$_SESSION['user']['id'].",'$type','$title','$html','$image','$url','active')"); } $ads=$db->query("SELECT * FROM ads WHERE advertiser_id=".$_SESSION['user']['id']." ORDER BY id DESC"); ?><!doctype html><html><head><meta charset='utf-8'><link rel='stylesheet' href='/public/assets/css/style.css'><title>Advertiser</title></head><body><div class='container'><h1>Advertiser Console</h1><div class='card'><h2>Buat Iklan</h2><form method='post'><label>Title</label><input name='title' required><label>Type</label><select name='type'><option value='html'>HTML</option><option value='image'>Image</option></select><label>HTML</label><textarea name='html' rows='5'></textarea><label>Image URL</label><input name='image'><label>Click URL</label><input name='click_url'><button>Simpan</button></form></div><div class='card'><h2>Iklan Saya</h2><table border='1' cellpadding='6'><tr><th>ID</th><th>Title</th><th>Views</th><th>Clicks</th><th>Status</th></tr><?php while($a=$ads->fetch_assoc()) echo "<tr><td>{$a['id']}</td><td>".e($a['title'])."</td><td>{$a['views']}</td><td>{$a['clicks']}</td><td>{$a['status']}</td></tr>"; ?></table></div></div></body></html>
