<?php require_once __DIR__.'/../core/helper.php'; require_once __DIR__.'/../core/db.php'; nxi_require_login(); $db=nxi_db(); $r=$db->query("SELECT id,title,views,clicks,ROUND(IFNULL(clicks/NULLIF(views,0),0)*100,2) ctr FROM ads ORDER BY id DESC LIMIT 200"); ?><!doctype html><html><head><meta charset='utf-8'><link rel='stylesheet' href='/public/assets/css/style.css'><title>Analytics</title></head><body><div class='container'><h1>Analytics</h1><div class='card'><table border='1' cellpadding='6'><tr><th>ID</th><th>Title</th><th>Views</th><th>Clicks</th><th>CTR %</th></tr><?php while($row=$r->fetch_assoc()) echo "<tr><td>{$row['id']}</td><td>".e($row['title'])."</td><td>{$row['views']}</td><td>{$row['clicks']}</td><td>{$row['ctr']}</td></tr>"; ?></table></div></div></body></html>
