<?php require_once __DIR__.'/../core/helper.php'; require_once __DIR__.'/../core/auth.php'; if($_SERVER['REQUEST_METHOD']==='POST'){ if(nxi_register($_POST['name']??'',$_POST['email']??'',$_POST['password']??'',$_POST['role']??'publisher')){ header('Location: /dashboard/login.php'); exit;} $err='Gagal register'; } ?><!doctype html><html><head><meta charset='utf-8'><link rel='stylesheet' href='/public/assets/css/style.css'><title>Register</title></head><body><div class='container'><div class='card'><h1>Register NXI Ads</h1><?php if(!empty($err)) echo '<div style="color:#b91c1c">'.$err.'</div>'; ?><form method='post'><label>Nama</label><input name='name' required><label>Email</label><input name='email' type='email' required><label>Password</label><input name='password' type='password' required><label>Role</label><select name='role'><option value='publisher'>Publisher</option><option value='advertiser'>Advertiser</option></select><button>Register</button></form><p>Sudah punya akun? <a href='/dashboard/login.php'>Login</a></p></div></div></body></html>
